package main

import (
	"fmt"
	"github.com/satindergrewal/kmdgo"
	"log"
)

func main() {
	var appName kmdgo.AppType
	appName = `komodo`

	var sblk kmdgo.SubmitBlock

	args := make(kmdgo.APIParams, 1)
	args[0] = ``
	//args[1] = `someworkid`
	fmt.Println(args)

	sblk, err := appName.SubmitBlock(args)
	if err != nil {
		fmt.Printf("Code: %v\n", sblk.Error.Code)
		fmt.Printf("Message: %v\n\n", sblk.Error.Message)
		log.Fatalln("Err happened", err)
	}

	fmt.Println("sblk value", sblk)
	fmt.Println("-------")
	fmt.Println(sblk.Result)
}
